mapper.select:
select * from (select zzz.*, rownum RNUM from( select b.business_no "business_no" ,b.business_name "business_name" , m.menu_no "menu_no" , m.menu_name "menu_name" , c.main_category_name "main_category_name" , c.mid_category_name "mid_category_name" , c.sub_category_name "sub_category_name" , m.menu_price "menu_price" , m.menu_cost "menu_cost" from menu m left join code_menu_category c on m.menu_category_code = c.menu_category_code left join business_info b on b.business_no=m.business_no where b.u_no='7'
) zzz ) where
RNUM>=${selectPageNo*rowCntPerPage-rowCntPerPage+1} and RNUM<=${selectPageNo*rowCntPerPage}

mapper.insert:
		
		insert into menu(
					menu_no
					, business_no
					, menu_name
					, menu_price
					, menu_cost
					, menu_category_code
			)values(
					(select nvl(max(menu_no),0)+1 from menu)
					,( select business_no
						from business_info 
						where 1=1 and business_no=#{business_no})
					, #{menu_name}
					, ${menu_price}
					, ${menu_cost}
					, (select menu_category_code from code_menu_category
					        where
					        	main_category_name=#{main_category_name}
					        	and mid_category_name=#{mid_category_name}
					        	and sub_category_name=#{sub_category_name})
   		)
